import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { Search, Filter, Star, MapPin, User } from 'lucide-react';

const Home = () => {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [availabilityFilter, setAvailabilityFilter] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [loading, setLoading] = useState(true);

  // Mock data - in real app this would come from API
  useEffect(() => {
    const mockUsers = [
      {
        id: 1,
        name: 'Sarah Johnson',
        location: 'New York, NY',
        photo: null,
        skillsOffered: ['JavaScript', 'React', 'Node.js'],
        skillsWanted: ['Python', 'Machine Learning'],
        availability: 'Weekends',
        rating: 4.8,
        publicProfile: true
      },
      {
        id: 2,
        name: 'Mike Chen',
        location: 'San Francisco, CA',
        photo: null,
        skillsOffered: ['Python', 'Data Analysis', 'SQL'],
        skillsWanted: ['JavaScript', 'Web Development'],
        availability: 'Evenings',
        rating: 4.5,
        publicProfile: true
      },
      {
        id: 3,
        name: 'Emily Davis',
        location: 'Austin, TX',
        photo: null,
        skillsOffered: ['Graphic Design', 'Photoshop', 'Illustrator'],
        skillsWanted: ['JavaScript', 'React'],
        availability: 'Weekdays',
        rating: 4.9,
        publicProfile: true
      },
      {
        id: 4,
        name: 'Alex Rodriguez',
        location: 'Miami, FL',
        photo: null,
        skillsOffered: ['Spanish', 'Marketing', 'Content Writing'],
        skillsWanted: ['Python', 'Data Science'],
        availability: 'Weekends',
        rating: 4.2,
        publicProfile: true
      }
    ];
    setUsers(mockUsers);
    setFilteredUsers(mockUsers);
    setLoading(false);
  }, []);

  useEffect(() => {
    let filtered = users.filter(user => user.publicProfile);
    
    if (searchTerm) {
      filtered = filtered.filter(user => 
        user.skillsOffered.some(skill => 
          skill.toLowerCase().includes(searchTerm.toLowerCase())
        ) ||
        user.skillsWanted.some(skill => 
          skill.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }
    
    if (availabilityFilter) {
      filtered = filtered.filter(user => 
        user.availability === availabilityFilter
      );
    }
    
    setFilteredUsers(filtered);
    setCurrentPage(1);
  }, [users, searchTerm, availabilityFilter]);

  const itemsPerPage = 6;
  const totalPages = Math.ceil(filteredUsers.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedUsers = filteredUsers.slice(startIndex, startIndex + itemsPerPage);

  const handleRequestClick = (userId) => {
    if (!user) {
      alert('Please log in to send a swap request');
      return;
    }
  };

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          Find Your Perfect Skill Swap
        </h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Connect with people who have the skills you need and offer your expertise in return.
        </p>
      </div>

      {/* Search and Filters */}
      <div className="card mb-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search skills..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="input-field pl-10"
            />
          </div>
          <div className="relative">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <select
              value={availabilityFilter}
              onChange={(e) => setAvailabilityFilter(e.target.value)}
              className="input-field pl-10"
            >
              <option value="">All Availability</option>
              <option value="Weekdays">Weekdays</option>
              <option value="Weekends">Weekends</option>
              <option value="Evenings">Evenings</option>
              <option value="Flexible">Flexible</option>
            </select>
          </div>
          <div className="text-sm text-gray-600 flex items-center justify-center">
            {filteredUsers.length} users found
          </div>
        </div>
      </div>

      {/* Users Grid */}
      {loading ? (
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
            {paginatedUsers.map((userProfile) => (
              <div key={userProfile.id} className="card hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center">
                      {userProfile.photo ? (
                        <img src={userProfile.photo} alt={userProfile.name} className="w-12 h-12 rounded-full" />
                      ) : (
                        <User className="text-primary-600" size={24} />
                      )}
                    </div>
                    <div>
                      <h3 className="font-semibold text-gray-900">{userProfile.name}</h3>
                      <div className="flex items-center text-sm text-gray-600">
                        <MapPin size={16} />
                        <span className="ml-1">{userProfile.location}</span>
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Star className="text-yellow-400" size={16} fill="currentColor" />
                    <span className="text-sm font-medium">{userProfile.rating}</span>
                  </div>
                </div>

                <div className="space-y-3">
                  <div>
                    <h4 className="text-sm font-medium text-gray-700 mb-1">Offers:</h4>
                    <div className="flex flex-wrap gap-1">
                      {userProfile.skillsOffered.map((skill, index) => (
                        <span key={index} className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                  
                  <div>
                    <h4 className="text-sm font-medium text-gray-700 mb-1">Wants:</h4>
                    <div className="flex flex-wrap gap-1">
                      {userProfile.skillsWanted.map((skill, index) => (
                        <span key={index} className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>

                  <div className="text-sm text-gray-600">
                    <span className="font-medium">Available:</span> {userProfile.availability}
                  </div>
                </div>

                <div className="mt-4 pt-4 border-t border-gray-200">
                  <div className="flex space-x-2">
                    <Link
                      to={`/profile/${userProfile.id}`}
                      className="btn-outline flex-1 text-center"
                    >
                      View Profile
                    </Link>
                    <button
                      onClick={() => handleRequestClick(userProfile.id)}
                      className="btn-primary flex-1"
                    >
                      Request Swap
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center space-x-2">
              <button
                onClick={() => setCurrentPage(currentPage - 1)}
                disabled={currentPage === 1}
                className="px-3 py-2 border border-gray-300 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
              >
                Previous
              </button>
              {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                <button
                  key={page}
                  onClick={() => setCurrentPage(page)}
                  className={`px-3 py-2 border rounded-lg ${
                    currentPage === page
                      ? 'bg-primary-600 text-white border-primary-600'
                      : 'border-gray-300 hover:bg-gray-50'
                  }`}
                >
                  {page}
                </button>
              ))}
              <button
                onClick={() => setCurrentPage(currentPage + 1)}
                disabled={currentPage === totalPages}
                className="px-3 py-2 border border-gray-300 rounded-lg disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50"
              >
                Next
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Home;
